package ch.bbw.zork;import java.util.Arrays;import java.util.List;public class CommandWords {	private List<String> validCommands = Arrays.asList("go", "quit", "help", "east","south", "west", "north","do","task" );	public boolean isCommand(String commandWord) {		return validCommands.stream()				.filter(c -> c.equals(commandWord))				.count()>0;	}	public String showAll() {		return String.join(" ", validCommands);	}}