package ch.bbw.zork;import java.util.HashMap;import java.util.ArrayList;public class Room {	private String description;	private HashMap<String, Room> exits;	private Task task;	public Room(String description) {		this.description = description;		this.exits = new HashMap<>();	}	public void setExits(Room north, Room east, Room south, Room west) {		exits.put("north", north);		exits.put("east", east);		exits.put("south", south);		exits.put("west", west);	}	public Task getTask() {		return task;	}	public void setTask(Task task) {		this.task = task;	}	public String getDescription() {		return description;	}	private String exitString() {		return "Exits:" + String.join(" ", exits.keySet());	}	public Room nextRoom(String direction) {		return exits.get(direction);	}}